Magpie

```json
{
  "application": "Magpie",
  "description": "Magpie is a collection of scripts for running Hadoop on traditional HPC systems. It aims to make it easier for users to run Hadoop on HPC systems by automating the process of setting up and configuring Hadoop.",
  "software_requirements": [
    {
      "name": "Java",
      "version": "1.8.0",
      "path": "/usr/lib/jvm/jre-1.8.0/"
    },
    {
      "name": "Python",
      "version": "3",
      "path": "/usr/bin/python3"
    },
    {
      "name": "Spark",
      "version": "3.5.0-bin-hadoop3",
      "path": "${HOME}/spark-${SPARK_VERSION}"
    },
    {
      "name": "Hadoop",
      "version": "not specified",
      "path": "not specified"
    },
    {
      "name": "Moab",
      "version": "not specified",
      "path": "not specified"
    },
    {
      "name": "PBS",
      "version": "not specified",
      "path": "not specified"
    },
    {
      "name": "pdsh",
      "version": "not specified",
      "path": "not specified"
    }
  ],
  "resource_requirements": [
    {
      "name": "nodes",
      "type": "integer",
      "description": "Number of nodes required for the job, including one node for the master/management/head node."
    },
    {
      "name": "walltime",
      "type": "string",
      "description": "Time limit for the job, in seconds or HH:MM:SS format. Should be larger than the sum of MAGPIE_STARTUP_TIME and MAGPIE_SHUTDOWN_TIME."
    },
    {
      "name": "partition",
      "type": "string",
      "description": "The partition to run the job on."
    },
    {
      "name": "queue",
      "type": "string",
      "description": "The batch queue to submit the job to."
    },
    {
      "name": "account",
      "type": "string",
      "description": "The account to charge the job to."
    },
    {
      "name": "local_scratch_dir",
      "type": "string",
      "description": "Directory for local scratch data. Can be specified to use local SSDs or NVRAM for better performance."
    },
    {
      "name": "staging_dir",
      "type": "string",
      "description": "Directory for staging files for Spark jobs when using Yarn. Should be a network location."
    }
  ]
}
