{
  "application": "SLURM",
  "details": {
    "language": "Bash",
    "purpose": "Generates SLURM job scripts for parallel scientific computing simulations",
    "input": {
      "case_file": "A file containing case definitions with columns for case name, algorithm mode, termination mode, termination value, boundary function, sample count, wall clock time, and configuration details (nodes, processors, interlines)",
      "executable_file": "Path to the executable file for the simulation",
      "output_directory": "Directory where the generated job scripts will be saved"
    },
    "output": {
      "job_scripts": "Generates individual job scripts for each case and configuration, named after the case and configuration parameters, with extensions .job"
    },
    "dependencies": {
      "SLURM": "Required for job scheduling and resource management",
      "mpiexec": "Used to launch parallel MPI processes",
      "spack": "Used to load MPI and other dependencies"
    },
    "resources": {
      "nodes": "Number of nodes to be requested for each job",
      "processors": "Number of processors per node to be requested for each job",
      "interlines": "Number of interlines to be used in the simulation",
      "wall_clock_time": "Maximum time allowed for each job"
    }
  }
}
