  "application": "ASR",
  "details": {
    "software": {
      "cuda": "10.2",
      "cudnn": "v7.6.5.32-prod-cuda-10.2",
      "miniconda3": "installed"
    },
    "resources": {
      "gpu": {
        "type": "v100",
        "number": 2,
        "mode": "exclusive_process"
      },
      "cpu": {
        "cores": 8
      },
      "memory": "32GB",
      "time": "23:59"
    }
  }
