{
  "application": "PyTorch",
  "details": {
    "resource_requirements": {
      "partition": "caspra",
      "nodes": 16,
      "tasks_per_node": 4,
      "cpus_per_task": 8,
      "gres": "dcu:4",
      "job_name": "torch-test"
    },
    "software_requirements": {
      "compiler": "devtoolset/7.3.1",
      "rocm": "2.9",
      "mpi": "hpcx/2.4.1/gcc-7.3.1",
